insert  into `p_sql_config`(`ID`,`SCENCE_CODE`,`ELE_NO`,`ELE_NAME`,`SQL`,`PARAM_NAME`,`SQL_TYPE`,`DATA_OPER_TYPE`,`DATA_OPER_TIME`) values (1,'TEST','TEST-01','测试一','select \'1\' name',NULL,'SELECT','I','2018-01-15 15:24:56'),(2,'TREE','TREE-001','获取类型树','SELECT * FROM p_note_type WHERE P_TYPE_NO = IFNULL(#{paramMap.typeCode,jdbcType=VARCHAR},\'000\')','typeCode','SELECT','I','2018-02-08 16:29:42'),(3,'NOTE','NOTE-001','获取笔记信息','SELECT \r\n  a.`NOTE_CONTAIN`,\r\n  a.`NOTE_NO`,\r\n  a.`NOTE_TITLE`,\r\n  a.`NOTE_TYPE`,\r\n  b.`TYPE_NAME` \r\nFROM\r\n  P_NOTE_MANAGE a,\r\n  p_note_type b \r\nWHERE a.`NOTE_TYPE` = b.`TYPE_NO` \r\nAND a.NOTE_NO = #{paramMap.noteNo,jdbcType=VARCHAR} ','noteNo','SELECT','I','2018-03-23 15:02:51'),(4,'NOTE','NOTE-002','获取笔记列表信息',' SELECT t.*,k.totalNum FROM (SELECT \r\n  a.`ACCOUNT_PHONE`,\r\n  a.`ACCOUNT_EMAIL`,\r\n  b.`USER_NO`,\r\n  b.`USER_NAME` CREATE_NAME,\r\n  DATE_FORMAT(c.`CREATE_TIME`,\'%Y-%m-%d %H:%I:%S\') CREATE_TIME,\r\n  c.`NOTE_NO`,\r\n  c.`NOTE_TITLE`,\r\n  d.`TYPE_NAME` NOTE_TYPE\r\nFROM\r\n  p_account a,\r\n  p_user_info b,\r\n  p_note_manage c,\r\n  p_note_type d \r\nWHERE a.`ACCOUNT_NO` = b.`ACCOUNT_NO` \r\n  AND c.`CREATE_PERSON` = b.`ACCOUNT_NO` \r\n  AND d.`TYPE_NO` = c.`NOTE_TYPE` \r\n  AND c.is_use = \'1\'\r\n) t, (SELECT \r\n  COUNT(*) totalNum\r\nFROM\r\n  p_account a,\r\n  p_user_info b,\r\n  p_note_manage c,\r\n  p_note_type d \r\nWHERE a.`ACCOUNT_NO` = b.`ACCOUNT_NO` \r\n  AND c.`CREATE_PERSON` = b.`ACCOUNT_NO` \r\n  AND d.`TYPE_NO` = c.`NOTE_TYPE` \r\n  AND c.is_use = \'1\') k',NULL,'SELECT','I','2018-03-23 15:05:36'),(5,'NOTE','NOTE-003','保存笔记信息','INSERT INTO p_note_manage (\r\n  NOTE_TITLE,\r\n  NOTE_CONTAIN,\r\n  CREATE_TIME,\r\n  CREATE_PERSON,\r\n  NOTE_TYPE,\r\n  NOTE_NO\r\n) \r\nVALUES\r\n  (\r\n    #{paramMap.noteTitle,jdbcType=VARCHAR},\r\n    #{paramMap.noteContain,jdbcType=VARCHAR},\r\n    NOW(),\r\n    #{paramMap.logAccountNo,jdbcType=VARCHAR},\r\n    #{paramMap.noteTypeNo,jdbcType=VARCHAR},\r\n    nextval (\'noteNoSeq\')\r\n  )',NULL,'SELECT','I','2018-03-23 15:58:32'),(6,'NOTE','NOTE-004','修改笔记信息','UPDATE \r\n  p_note_manage \r\nSET\r\n  NOTE_TITLE = #{paramMap.noteTitle,jdbcType=VARCHAR},\r\n  NOTE_CONTAIN = #{paramMap.noteContain,jdbcType=VARCHAR},\r\n  UPDATE_TIME = NOW(),\r\n  NOTE_TYPE = #{paramMap.noteTypeNo,jdbcType=VARCHAR},\r\n  data_oper_type = \'U\' \r\nWHERE NOTE_NO = #{paramMap.noteNo,jdbcType=VARCHAR}','noteNo,noteTitle,noteContain,noteTypeNo','SELECT','I','2018-03-23 15:58:35'),(7,NULL,NULL,NULL,NULL,NULL,'SELECT','I','2018-03-23 15:58:39');

